import styled from 'styled-components'
import {Link} from 'react-router-dom'

export const Container = styled.div`
    min-height: 692px;
    position: fixed;
    bottom: 0;
    left: 0;
    right: 0;
    top: 0;
    z-index: 0;
    overflow: hidden;
    background: linear-gradient(
    109deg,
    rgba(249, 166, 108, 1) 0%,
    rgba(241, 122, 126, 1) 100%
    );
`;

export const FormWrap = styled.div`
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;

    @media screen and (max-width: 400px) {
        height: 80%;
    }
`

export const Icon = styled(Link)`
    margin-left: 32px;
    margin-top: 32px;
    text-decoration: none;
    color: var(--white);
    font-weight: 700;
    font-size: 32px;

    @media screen and (max-width: 480px) {
        margin-left: 16px;
        margin-top: 8px;
    }
`

export const FormContent = styled.div`
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;

    @media screen and (max-width: 480px) {
        padding: 10px;
    }
`

export const Form = styled.form`
    background: var(--grey);
    max-width: 400px;
    height: auto;
    width: 100%;
    z-index: 1;
    display: grid;
    margin: 0 auto;
    padding: 80px 32px;
    border-radius: 4px;
    box-shadow: 0 1px 3px rgba(249, 166, 108, 1);

    @media screen and (max-width: 400px) {
    padding: 32px 32px;
    }
`
export const FormH1 = styled.h1`
    margin-bottom: 40px;
    color: var(--white);
    font-size: 20px;
    font-weight: 400;
    text-align: center;
`
export const FormLabel = styled.label`
    margin-bottom: 8px;
    color: var(--white);
    font-size: 20px;
`
export const FormInput = styled.input`
    padding: 16px 16px;
    margin-bottom: 32px;
    border: none;
    border-radius: 4px;
`
export const FormButton = styled.button`
    background: var(--pink);
    padding: 16px 0;
    border: none;
    border-radius: 4px;
    color: var(--white);
    font-size: 24px;
    cursor: pointer;
` 
export const Text = styled.span`
    text-align: center;
    margin-top: 24px;
    color: var(--white);
    font-size: 20px;
`
